export function IconUser({ className }: { className?: string }) {
  const session = useSession();

  return (
    <Avatar className={cn("h-8 w-8", className)}>
      <AvatarImage src={session.data?.user?.image || ""} />
      <AvatarFallback>
        {session.data?.user?.name
          ?.split(" ")
          .map((n) => n[0])
          .join("")
          .toUpperCase() || "AU"}
      </AvatarFallback>
    </Avatar>
  );
}

